import React from 'react';
import { Query, Mutation } from 'react-apollo';
import { gql } from 'apollo-boost';

import CheckoutItem from './checkout-item.component';
import { Graphql } from '../../graphql/resolvers';
import { removeItemFromCart, clearItemFromCart } from '../../graphql/cart.utils';

const ADD_ITEM_TO_CART = gql`
  mutation AddItemToCart($item: Item!) {
    addItemToCart(item: $item) @client

  }
`;


const CheckoutItemContainer = props => (
  <Mutation mutation={ADD_ITEM_TO_CART}>
    {
      (addItemToCart) => {
        console.log(addItemToCart);
        return (
          <CheckoutItem
            {...props}
            addItem={item => addItemToCart({ variables: { item } })}
            removeItem={item => removeItemFromCart(cartItems, { item })}
            clearItem={item => clearItemFromCart(cartItems, { item })}
          />
        )
      }
    }
  </Mutation>
);

export default CheckoutItemContainer;
